examples.code:
const fullName = computed({
  get: () => `${firstName.value} ${lastName.value}`,
  set: (newValue) => {
    [firstName.value, lastName.value] = newValue.split(' ')
  }
})

import { ref, watch } from 'vue'
const count = ref(0)
watch(count, (newCount, oldCount) => {
  console.log(`count changed from ${oldCount} to ${newCount}`)
})

const state = reactive({ count: 0 })
watch(
  () => state.count,
  (count) => { console.log(`count is: ${count}`) }
)

const state = reactive({ nested: { count: 0 } })
watch(state, (newState) => {
  // 默认情况下，对响应式对象的侦听是深度的
  console.log('State changed:', newState)
}, { deep: true }) // 对于 ref 包含的对象，需要 deep:true

import { ref, watchEffect } from 'vue'

const userID = ref(1)

watchEffect(async () => {
  // 当 userID.value 变化时，此函数会自动重新执行
  const response = await fetch(`https://api.example.com/users/${userID.value}`)
  const data = await response.json()
  console.log(data)
})

import { onMounted } from 'vue'
onMounted(() => {
  console.log('Component has been mounted!')
  // 通常在这里执行 API 调用
})

import { onUnmounted } from 'vue'
let intervalId
onMounted(() => {
  intervalId = setInterval(() => { /* ... */ }, 1000)
})
onUnmounted(() => {
  console.log('Component is being unmounted.')
  clearInterval(intervalId)
})

import { onUpdated } from 'vue'
onUpdated(() => {
  console.log('Component DOM has been updated.')
})

