// 1. 定义指令 (main.js 或 directives.js)
const app = createApp(App)
app.directive('focus', {
  // 当被绑定的元素挂载到 DOM 中时
  mounted(el) {
    el.focus()
  }
})

// 2. 在模板中使用
// <input v-focus />

// 1. 定义指令
app.directive('color', (el, binding) => {
